import{_ as t,c as a,o,aO as i}from"./chunks/framework.DoXxOPvI.js";const s="/LeslieBlog/assets/AManCalledOttoDecidedToDie2.jFyxx9LH.png",l="/LeslieBlog/assets/AManCalledOttoDecidedToDie1.BqudomKo.png",n="/LeslieBlog/assets/AManCalledOttoDecidedToDie5.B7iCLU5v.png",d="/LeslieBlog/assets/AManCalledOttoDecidedToDie4.BpU0zLfp.png",r="/LeslieBlog/assets/AManCalledOttoDecidedToDie3.BOkCem-8.png",C=JSON.parse('{"title":"A Man Called Otto","description":"","frontmatter":{},"headers":[],"relativePath":"movies/110.movie.md","filePath":"movies/110.movie.md"}'),m={name:"movies/110.movie.md"};function p(c,e,_,g,h,u){return o(),a("div",null,e[0]||(e[0]=[i('<h1 id="a-man-called-otto" tabindex="-1">A Man Called Otto <a class="header-anchor" href="#a-man-called-otto" aria-label="Permalink to &quot;A Man Called Otto&quot;">​</a></h1><p>杀死那个叫奥托的男人[doge]<br> 笔者这里看的是美版 后来经朋友提醒才发现有两个版本<br> 明天再看看瑞典原版</p><p>笔者后来查了下，根据奥托墓碑上的出生年份1955年，假设其大约是18-25岁参兵即1973-1980，约莫是越南战争结束前后。1973年美国废除了征兵制度，奥托的参军只能是自愿参军，而彼时之美国，青年们的反战情绪严重，摇滚文化盛行（如约翰列侬的give peace a chance），可奥托却为无法参军所懊恼，可见奥托确实是一个活在旧时代的人【笑】（不过奥托参军的动机也有经济因素的考量：父母双亡的他无法从兜里多掏出一份火车票钱、请心仪的女子共进晚餐却无法为自己负担一份主食），他渴望参军，却又因病参不了军。<br> 奥托，再次成为了那个局外人——他既不反对参军，又没能真正参军。</p><p><img src="'+s+'" alt="这里本该有一张图片！！请刷新网页或者耐心等待~~"><em>奥托从人人嫌到人人爱 最终死于hypertrophic cardiomyopathy（一种心脏过大导致的的遗传性心脏病 这把契诃夫之枪早在参军体检时就一闪而过） 所幸 他最终不是死于轻生 从社区中他得到了生的动力</em><img src="'+l+'" alt="这里本该有一张图片！！请刷新网页或者耐心等待~~"><em>引乔哲一句——白纸黑字之间 你的出现是第三种颜色。 事实上 奥托遇上妻子时 正是他一生中最黑暗的时候 母亲早早地离开了他 父亲又在两个月前溘然长逝 参军又被拒绝</em><img src="'+n+'" alt="这里本该有一张图片！！请刷新网页或者耐心等待~~"><em>奥托热衷于探究事物之理 热衷于有条不紊的生活 他每日例行的round也正是为着社区的正常和谐运转</em><img src="'+d+'" alt="这里本该有一张图片！！请刷新网页或者耐心等待~~"><em>全片唯一得到奥托官方认证并非idiot的人 两次拯救奥托 若无她的善良热情 奥托早就走了 她的出现让奥托意识到了他还有需要他来照顾的人 她也在奥托的帮助下学会了车（奥托的鼓励使她重获了信心） 正如奥托所说 一个能够千里迢迢从墨西哥来到美国、带着笨笨的丈夫拉扯大两个小女孩 还即将有一个小男孩的女人 怎么可能学不会开车</em><img src="'+r+'" alt="这里本该有一张图片！！请刷新网页或者耐心等待~~"><em>奥托是富有责任心的 奥托总觉得他必须得pull his weight 他年轻时想要参军 与妻子的相遇源于热心提醒 工作恪尽职守 做事一丝不苟 每日例行社区的round 这是一个完美的男人 却被公司的人排挤 因为奥托是傲慢的 他热衷于帮助他人 却也认为其他做不好事的人是idiot 他做事一丝不苟 却也直来直去对他人出言不逊 他将自身的不幸归咎于他人的愚蠢 他忽视了那些被他称为idiot的人对他释放的善意他 他渴望付出而傲娇地拒绝接受善意。<br> 奥托是渴望融入集体、为集体做贡献的 他终其一生都在寻找一种认同 却也总是被排斥在集体之外 年少参军失败 中年朋友反目，晚年丧偶、同事排挤 他的轻生念头绝不仅仅来自妻子的去世 事实上他做出轻生行动时（去超市购买绳子）便已被公司的同事排挤 他的刻薄、愤怒也使得愈来不满于社区的环境 在这个曾经亲密无间的朋友（那对黑人夫妇）陌路、无人对他为社区做的贡献表以认可的社区、公司 他已经逐渐丧失了那种来自集体的认同感 所幸 女主夫妇的出现使她意识到了自己仍是被需要的 他仍能做出一些贡献、仍有人认可他做的贡献。最终 带着这种认可 他得以继续着他的生活。</em></p><p>有 <span id="busuanzi_page_pv">加载中...请刷新~~~</span> 个小伙伴看过本文</p><p>首次编辑时间：202508151140 最后编辑时间：202508191645</p>',6)]))}const D=t(m,[["render",p]]);export{C as __pageData,D as default};
